
function masonry(){$(".sw-masonry").each(function(){var n=$(this);var j=n.width();var l=n.children(".col");if(l.length>0){l.each(function(){$(this).remove()})}var k=n.children(".item");k.each(function(){$(this).hide()});var a=k.eq(0).width();var e=10;var g=k.length;var m=Math.floor(j/(a+(e*2)));var c=Math.round(g/m);if(c<1){c=1}for(var d=1;d<=m;d++){var b=$("<div>").addClass("col col-"+d).css({"margin-right":e+"px","margin-left":e+"px"});g-=c;for(var f=0;f<c;f++){if(g===1){var h=0}else{var h=d-1+f*m}b.append(k.eq(h).clone().show());k.eq(h).hide()}n.append(b)}if(g>0){var k=n.children(".item");for(var d=1;d<=g;d++){n.find(".col-"+d).append(k.eq(d-1))}}})};