
function masonry(){$(".sw-masonry").each(function(){var m=$(this);var j=m.width();var k=m.children(".item");var a=k.eq(0).width();var e=10;var g=k.length;var l=Math.floor(j/(a+(e*2)));var c=Math.round(g/l);for(var d=1;d<=l;d++){var b=$("<div>").addClass("col col-"+d).css({"margin-right":e+"px","margin-left":e+"px"});g-=c;for(var f=0;f<c;f++){var h=d-1+f*l;b.append(k.eq(h))}m.append(b)}if(g>0){var k=m.children(".item");for(var d=1;d<=g;d++){m.find(".col-"+d).append(k.eq(d-1))}}})};